import React, { Component } from "react";
import { Route } from "react-router-dom";
import axiosWithAuth from "../utils/axiosWithAuth";
import axios from "axios";
import { Link } from "react-router-dom";
import GatedContentNav from "./GatedContentNav";

class Trips extends Component {
  constructor() {
    super();
    this.state = {
      trips: [],
      filter: {
        professional: false
      }
    };
    console.log(this);
  }

  componentDidMount() {
    const endpoint = "https://ls-guidr.herokuapp.com/api/trips";
    axios
      .get(endpoint)
      .then(res => {
        const trips = res.data;
        this.setState({
          trips,
          professional: trips.map(trip => trip.professional)
        });
      })
      .catch(error => {
        console.error("USERS ERROR", error);
      });
  }

  selectTabHandler = (e, filter) => {
    // this function should take in the tab and update the state with the new tab.
    console.log(e);
    console.log(filter);

    // this.setState({
    //   professional: tab
    // });
  };

  render() {
    return (
      <div>
        <div>
          <GatedContentNav />
        </div>
        <h1>My Trips</h1>
        <div>
          <button onClick={e => this.selectTabHandler}>Private</button>
          <button onClick={e => this.selectTabHandler}>Professional</button>
        </div>
        <div>
          {this.state.trips.map(trip => (
            <div key={trip.id}>
              <p>{trip.title}</p>
              <p>{trip.description}</p>
              <p>{trip.professional}</p>
              <Link to={`/trips/${trip.id}`}>
                <p>Details</p>
              </Link>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default axiosWithAuth(Trips);
