import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import GatedContentNav from "./GatedContentNav";

export default class Trip extends Component {
  constructor() {
    super();
    this.state = {
      trips: []
    };
    console.log(this);
  }

  componentDidMount() {
    const endpoint = "https://ls-guidr.herokuapp.com/api/trips";
    axios
      .get(endpoint)
      .then(res => {
        this.setState({ trips: res.data });
      })
      .catch(error => {
        console.error("USERS ERROR", error);
      });
  }

  render() {
    trip = this.state.trips.find(
      trip => `${trip.id}` === this.props.match.params.id
    );
    if (!trip) return <h3>Loading data...</h3>;

    return (
      <div>
        <p>{this.trip.description}</p>
      </div>
    );
  }
}
