import React, { Component } from "react";
import axios from "axios";
import GatedContentNav from "./GatedContentNav";

class UpdateProfile extends Component {
  constructor() {
    super();
    this.state = {
      user: [],
    };
  }

  componentDidMount() {
    const endpoint = "https://ls-guidr.herokuapp.com/api/profile";
    axios
      .get(endpoint)
      .then(result => {
        const user = result.data[0];
        this.setState({
          user,
        });
      })
      .catch(error => {
        console.error("ERROR", error);
      });
  }

  updateUser = updatedUser => {
    console.log(updatedUser);
    axios
      .put(
        `https://ls-guidr.herokuapp.com/api/profile/`,
        updatedUser
      )
      .then(res => {
        const user = res.data;
        this.setState({ 
          user
        });
        console.log("success!");

        // redirect
        this.props.history.push(`/profile/`);
      })
      .catch(err => {
        console.log(err);
      });
  };

  handleChange = e => {
    e.preventDefault();console.log(e.target.value)
    const state = {...this.state, user: {...user,[e.target.name]: e.target.value}}
    this.setState({ 
        user: state
    });
  };

  onSubmitEditedUser = e => {
    e.preventDefault();
    this.updateUser(this.state.user);
  };

  render() {
    return (
      <div>
        <div className="form-container">
          <h2>Update Profile</h2>
          <form className="form" onSubmit={this.onSubmitEditedUser}>
          <input
              name="username"
              placeholder="username"
              onChange={this.handleChange}
              value={this.state.user.first_name}
            />
            <input
              type="text"
              name="age"
              placeholder="age"
              onChange={this.handleChange}
              value={this.state.user.age}
            />
            <input
              type="text"
              name="certs"
              placeholder="certs"
              onChange={this.handleChange}
              value={this.state.user.certs}
            />
            <input
              type="text"
              name="profile_text"
              placeholder="Brief Description"
              onChange={this.handleChange}
              value={this.state.user.profile_text}
            />
            <input
              type="text"
              name="years_of_exp"
              placeholder="Years of Experience"
              onChange={this.handleChange}
              value={this.state.user.years_of_exp}
            />
           <button onClick={this.updateProfile}>Update Profile</button>
          </form>
        </div>
      </div>
    );
  }
}

export default UpdateProfile;
