import React, { Component } from "react";
import { Route } from "react-router-dom";
import axiosWithAuth from "../utils/axiosWithAuth";
import axios from "axios";
import { Link } from "react-router-dom";
import GatedContentNav from "./GatedContentNav";

class Trips extends Component {
  constructor() {
    super();
    this.state = {
      allTrips: [],
      trips: [],
      filter: {}
    };
    console.log(this);
  }

  componentDidMount() {
    const endpoint = "https://ls-guidr.herokuapp.com/api/trips";
    axios
      .get(endpoint)
      .then(res => {
        const trips = res.data;
        this.setState({
          allTrips: trips,
          trips
        });
      })
      .catch(error => {
        console.error("USERS ERROR", error);
      });
  }

  getFilteredTrips = filter => {
    const mergedWithFilter = JSON.stringify({ ...trip, ...filter });
    const tripMatch = JSON.stringify(trip);
    return this.state.allTrips.filter(trip => {
      return mergedWithFilter === tripMatch;
    });
  };

  updateFilter = filter => {
    const filteredTrips = this.getFilteredTrips(filter);
    this.setState({
      trips: filteredTrips
    });
  };

  render() {
    return (
      <div>
        <div>
          <GatedContentNav />
        </div>
        <h1>My Trips</h1>
        <div>
          <button
            className={`tab${
              props.selectedTab === props.tab ? " active-tab" : ""
            }`}
            onClick={() => this.updateFilter()}
          >
            All
          </button>
          <button onClick={() => this.updateFilter({ professional: 1 })}>
            Private
          </button>
          <button onClick={() => this.updateFilter({ professional: 0 })}>
            Professional
          </button>
        </div>
        <div>
          {this.state.trips.map(trip => (
            <div key={trip.id}>
              <p>{trip.title}</p>
              <p>{trip.description}</p>
              <Link to={`/trips/${trip.id}`}>
                <p>Details</p>
              </Link>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default axiosWithAuth(Trips);
