import React, { Component } from "react";
import { Route } from "react-router-dom";
import axiosWithAuth from "../utils/axiosWithAuth";
import axios from "axios";
import { Link } from "react-router-dom";
import GatedContentNav from "./GatedContentNav";
import "./GatedContent.css";

class Trips extends Component {
  constructor() {
    super();
    this.state = {
      allTrips: [],
      trips: [],
      filter: {}
    };
    console.log(this);
  }

  componentDidMount() {
    const endpoint = "https://ls-guidr.herokuapp.com/api/trips";
    axios
      .get(endpoint)
      .then(res => {
        const trips = res.data;
        this.setState({
          allTrips: trips,
          trips
        });
      })
      .catch(error => {
        console.error("USERS ERROR", error);
      });
  }

  getFilteredTrips = filter => {
    return this.state.allTrips.filter(trip => {
      return JSON.stringify({ ...trip, ...filter }) === JSON.stringify(trip);
    });
  };

  updateFilter = (e, filter) => {
    console.log(e.target.id);
    const filteredTrips = this.getFilteredTrips(filter);
    this.setState({
      trips: filteredTrips,
      activeTab: true
    });
  };

  render() {
    console.log(this.state);
    return (
      <div>
        <div>
          <GatedContentNav />
        </div>
        <h1>My Trips</h1>
        <div>
          <button
            id="all"
            className={`tab${
              this.state.activeTab === "all" ? " active-tab" : ""
            }`}
            onClick={e => this.updateFilter(e)}
          >
            All
          </button>
          <button
            id="private"
            className={`tab${
              this.state.activeTab === "private" ? " active-tab" : ""
            }`}
            onClick={e => this.updateFilter(e, { professional: 1 })}
          >
            Private
          </button>
          <button
            id="professional"
            className={`tab${
              this.state.activeTab === "professional" ? " active-tab" : ""
            }`}
            onClick={e => this.updateFilter(e, { professional: 0 })}
          >
            Professional
          </button>
        </div>
        <div>
          {this.state.trips.map(trip => (
            <div key={trip.id}>
              <p>{trip.title}</p>
              <p>{trip.description}</p>
              <Link to={`/trips/${trip.id}`}>
                <p>Details</p>
              </Link>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default axiosWithAuth(Trips);
