import React, { Component } from "react";
import { Route } from "react-router-dom";
import axiosWithAuth from "../utils/axiosWithAuth";
import axios from "axios";
import { Link } from "react-router-dom";
import GatedContentNav from "./GatedContentNav";

class Trips extends Component {
  constructor() {
    super();
    this.state = {
      allTrips: [],
      trips: [],
      filter: {
        professional: 1
      }
    };
    console.log(this);
  }

  componentDidMount() {
    const endpoint = "https://ls-guidr.herokuapp.com/api/trips";
    axios
      .get(endpoint)
      .then(res => {
        const trips = res.data;
        this.setState({
          allTrips: trips,
          trips
        });
      })
      .catch(error => {
        console.error("USERS ERROR", error);
      });
  }

  updateFilter = filter => {
    this.setState({
      filter: { ...this.state.filter, filter }
    });
    console.log(this.state.filter);
  };

  // matchesFilter = (filter, trip) => {
  //   function isTrue(value) {
  //     return value === "true";
  //   }
  //   const matches = [];
  //   Object.keys(filter).forEach(key => {
  //     matches.push((trip[key] === filter[key]).toString());
  //   });
  //   return matches.every(isTrue);
  // };

  // getFilteredTrips = filter => {
  //   return this.state.allTrips.filter(t => this.matchesFilter(filter, t));
  // };

  // selectTabHandler = (e, filter) => {
  //   // this function should take in the tab and update the state with the new tab.
  //   this.setState({
  //     ...this.state.filter,
  //     filter,
  //     trips: [...this.getFilteredTrips(filter)]
  //   });
  // };

  render() {
    return (
      <div>
        <div>
          <GatedContentNav />
        </div>
        <h1>My Trips</h1>
        <div>
          <button onClick={() => this.updateFilter({ professional: 1 })}>
            Private
          </button>
          <button onClick={() => this.updateFilter({ professional: 0 })}>
            Professional
          </button>
        </div>
        <div>
          {this.state.trips.map(trip => (
            <div key={trip.id}>
              <p>{trip.title}</p>
              <p>{trip.description}</p>
              <p>{trip.professional}</p>
              <Link to={`/trips/${trip.id}`}>
                <p>Details</p>
              </Link>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default axiosWithAuth(Trips);
